import {useState} from 'react';

const initialUserInput = {
    'current-savings': 10000,
    'yearly-contribution': 1200,
    'expected-return': 7,
    duration: 10
}

const UserInput = () => {

    const [userInput, setUserInput] = useState(initialUserInput)

    const submitHandler = (event) => {
        event.preventDefault();
        console.log('Submit')
    }  

    const resetHandler = (event) => {
        setUserInput({
            'current-savings': 10000,
            'yearly-contribution': 1200,
            'expected-return': 7,
            duration: 10
        })
    }

    const inputChangeHandler = (input, value) => {
        setUserInput((prevInput) => {
            return{
                ...prevInput,
                [input]: value,
            }
        });
    }

    return (
        <form onSubmit={submitHandler} className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input onChange={(event) => {inputChangeHandler('current-savings', event.target.value)}} type="number" id="current-savings" />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" onChange={(event) => {inputChangeHandler('yearly-contribution', event.target.value)}} id="yearly-contribution" />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number"  onChange={(event) => {inputChangeHandler('expected-return', event.target.value)}} id="expected-return" />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number"  onChange={(event) => {inputChangeHandler('duration', event.target.value)}} id="duration" />
          </p>
        </div>
        <p className="actions">
          <button onClick={resetHandler} type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    )

}


export default UserInput;
