import { useState, useEffect } from 'react';


const useCounter = (forwards) => {
  const [counter, setCounter] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCounter();
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return counter;

};

export default useCounter;
